import { Pipe, PipeTransform } from '@angular/core';
import { AppConstants } from '../../models/constant/app-constant';

@Pipe({
  name: 'customCurrency',
})
export class CustomCurrencyPipe implements PipeTransform {
  transform(value: number, locale = AppConstants.COMMON.DEFAULT_DATE_LOCAL): string | null {
    if (value == null) {
      return null;
    }
    const formattedValue = new Intl.NumberFormat(locale, {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(value);
    return formattedValue;
  }
}

import { DatePipe } from '@angular/common';
import { Pipe, PipeTransform } from '@angular/core';
import { AppConstants } from '../../models/constant/app-constant';

export enum DATE_FORMATS {
  DAYDATE,
  MONTHDATE,
  FULLDATE, // default
}
@Pipe({
  name: 'dateFormat',
})
export class DateFormatPipe implements PipeTransform {
  constructor(public datePipe: DatePipe) {}

  transform(value?: string, type?: DATE_FORMATS) {
    let format = AppConstants.COMMON.DEFAULT_DATE_FORMAT; // => 'dd.MM.yyyy'

    if (type == DATE_FORMATS.DAYDATE) {
      format = 'dd';
    }

    if (type == DATE_FORMATS.MONTHDATE) {
      format = 'MM.yyyy';
    }

    return this.datePipe.transform(value, format);
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'phoneNumber',
})
export class PhoneNumberPipe implements PipeTransform {
  transform(value: string): string {
    if (!value) {
      return value;
    }

    // Remove non-numeric characters from the input phone number
    const numericValue = value.replace(/\D/g, '');

    // Format the phone number as "+CC NPA NXX XXXX"
    const countryCode = numericValue.slice(0, 2);
    const npa = numericValue.slice(2, 5);
    const nxx = numericValue.slice(5, 8);
    const xxxx = numericValue.slice(8);

    return `+${countryCode} ${npa} ${nxx} ${xxxx}`;
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'truncate',
})
export class TruncatePipe implements PipeTransform {
  transform(text: string, maxLength: number): string {
    if (!text) {
      return ''; // Return empty string for undefined or empty text
    }

    if (text.length > maxLength) {
      return text.substring(0, maxLength - 3) + '...';
    }

    return text;
  }
}

